console.log('Start Email Tachos node');

//var fs = context.global.fs;
var Imap = context.global.imap;
var base64 = context.global.base64Stream;
var streamm = context.global.stream;
var request = context.global.request;

// get organization active
var organization = msg.payload;

if (organization.mailboxEmail === undefined)
    return;

console.log('Download email tachos for Organization Id: ' + organization.id + ' with name ' + organization.name);

var imap = new Imap({
    user: organization.mailboxEmail,
    password: organization.mailboxPassword,
    host: 'imap.gmail.com',
    port: 993,
    tls: true
});

function findAttachmentParts(struct, attachments) {
    attachments = attachments ||  [];

    for (var i = 0, len = struct.length, r; i < len; ++i) {
        if (Array.isArray(struct[i])) {
            findAttachmentParts(struct[i], attachments);
        } else {
            if (struct[i].disposition && ['INLINE', 'ATTACHMENT'].indexOf(struct[i].disposition.type) > -1) {
                attachments.push(struct[i]);
            }
        }
    }

    return attachments;
}

function buildAttMessageFunction(attachment) {
    var filename = attachment.params.name;
    var encoding = attachment.encoding;

    return function (message, seqno) {
        var prefix = '(#' + seqno + ') ';

        message.on('body', function(stream, info) {
            //Create a write stream so that we can stream the attachment to file;
            console.log(prefix + 'Streaming this attachment to file', filename, info);

            // create final stream
            var bufferStream = new streamm.PassThrough();
            if (encoding === 'BASE64') {
                //the stream is base64 encoded, so here the stream is decode on the fly and piped to the write stream (file)
                stream.pipe(base64.decode()).pipe(bufferStream);
            } else  {
                //here we have none or some other decoding streamed directly to the file which renders it useless probably
                stream.pipe(bufferStream);
            }

            // recover the new stream and pass to the next node
            var chunks = [];
            bufferStream.on('data', function(data) {
                chunks.push(data);
            });

            bufferStream.on('end', function() {
                var body = Buffer.concat(chunks);
                console.log('File size: ' + body.length);

                // pass to the next node
                var formData = {"organization": JSON.stringify(organization), 
                                "file": {"value": body, "options": {"filename": filename}}};

                console.log("Organization: " + formData);

                request.post({url:'http://192.168.1.100:3000/services/rest/files/uploader', formData: formData}, function(err, httpResponse, body) {
                    if (err)
                        node.send(err);

                    console.log('Upload successful!  Server responded with:', body);

                    msg.payload = body;
                    node.send(msg);
                });                            
            });
        });

        message.once('end', function() {
            console.log(prefix + 'Finished attachment %s', filename);
        });
    };
}

imap.once('ready', function() {
    imap.openBox('INBOX', false, function(err, box) {
        if (err) throw err;

        console.log('Searching emails from: ', organization.name);
        imap.search([ 'UNSEEN', ['SINCE', 'May 24, 2016'] ], function(err, results) {
            if (err) throw err;

            if (results.length === 0)
                return;

            //var f = imap.seq.fetch('1:3', {struct: true, markSeen:true, bodies: ['HEADER.FIELDS (FROM TO SUBJECT DATE)']});
            var f = imap.fetch(results, { struct: true, markSeen:true, bodies: '' });

            f.on('message', function (msg, seqno) {
                console.log('Message #%d', seqno);
                var prefix = '(#' + seqno + ') ';

                msg.on('body', function(stream, info) {
                    var buffer = '';
                    stream.on('data', function(chunk) {
                        buffer += chunk.toString('utf8');
                    });
                    stream.once('end', function() {
                        console.log(prefix + 'Parsed header: %s', Imap.parseHeader(buffer));
                    });
                });

                msg.once('attributes', function(attrs) {
                    var attachments = findAttachmentParts(attrs.struct);

                    if (attachments === undefined)
                        return;

                    console.log(prefix + 'Has attachments: %d', attachments.length);
                    for (var i = 0, len=attachments.length ; i < len; ++i) {
                        var attachment = attachments[i];
                        /*This is how each attachment looks like {
                             partID: '2',
                             type: 'application',
                             subtype: 'octet-stream',
                             params: { name: 'file-name.ext' },
                             id: null,
                             description: null,
                             encoding: 'BASE64',
                             size: 44952,
                             md5: null,
                             disposition: { type: 'ATTACHMENT', params: { filename: 'file-name.ext' } },
                             language: null
                             }
                         */
                        console.log(prefix + 'Fetching attachment %s', attachment.params.name);

                        if (attachment.params.name === undefined)
                            return;

                        if (attachment.params.name.toUpperCase().indexOf(".TGD") < 0 &&
                            attachment.params.name.toUpperCase().indexOf(".DDD") < 0 &&
                            attachment.params.name.toUpperCase().indexOf(".C1V") < 0)
                                return;

                        var f = imap.fetch(attrs.uid , {bodies: [attachment.partID], struct: true });

                        //build function to process attachment message
                        f.on('message', buildAttMessageFunction(attachment));
                    }
                });
                msg.once('end', function() {
                    console.log(prefix + 'Finished email');
                });
            });

            f.once('error', function(err) {
                console.log('Fetch error: ' + err);
            });

            f.once('end', function() {
                console.log('Done fetching all messages!');
                imap.end();
            });
        });
    });
});

imap.once('error', function(err) {
    console.log(err);
});

imap.once('end', function() {
    console.log('Connection ended');
});

imap.connect();

return;

console.log('Check Driver Tachos Reading');

var request = context.global.request;

// get driver active
var driver = msg.payload;
var MAX_DAYS = 3;

if (driver.length === 0)
    return;

if (driver.days < MAX_DAYS) {
    var eventt = {
                  "organizationId": driver.organizationId,
                  "description": "El conductor " + driver.name + " le quedan menos de tres días para descargar la tarjeta de conductor",
                  "date": new Date(),
                  "type": "Warning",
                  "confirm": false
                 };

    request.post({ url:'http://192.168.1.100:3000/services/rest/Events/events/pushEvent', 
                   json: true,
                   body: eventt}, function(err, httpResponse, body) {
        if (err)
            node.send(err);

        console.log('Alarm sent successfuly!. Server responded with:', body);

        msg.payload = body;
        node.send(msg);
    }); 
}

return msg;

